import * as Msal from 'msal';

// export default function Auth() {

const config = {
    auth: {
        clientId: "ce5d1a2b-20a7-4f63-adde-6f0168cc29e5",
        authority: "https://b2cmern.b2clogin.com/b2cmern.onmicrosoft.com/B2C_1_signupsignin",
        redirectUri: 'http://localhost:3000',
        validateAuthority: false,
        navigateToLoginRequestUrl: false
    },
    cache: {
        cacheLocation: "localStorage",
        storeAuthStateInCookie: true
    }
};

const loginRequest = {
    scopes: ["https://b2cmern.onmicrosoft.com/mern/demo.read"],
    loginHint: null
};


let context = new Msal.UserAgentApplication(config);
context.handleRedirectCallback((error, response) => {
    console.log('handleRedirectCallback');

    if (error) {
        console.error(error);
    } else if (window.parent === window) {
        console.log(response.tokenType);

        if (response.tokenType === 'access_token' && response.accessToken) {
            // resolve(response.accessToken);
        }
    }
});

export const b2cSignOut = () => {
    context.logout();
}

export const b2cGetAccount = () => {
    if (context.getAccount()) {
        context.acquireTokenSilent(loginRequest).then((response) => {
            if (response.accessToken) {
                console.log(response.accessToken);
            }
        }).catch((_error) => {
            context.acquireTokenRedirect(loginRequest);
        });
    }

    return context.getAccount();
}

export const b2cSignIn = () => {
    return new Promise((resolve, reject) => {
        console.log('inside the promise');

        if (!context.isCallback(window.location.hash)) {
            console.log('not callback');

            if (context.getAccount()) {
                context.acquireTokenSilent(loginRequest).then((response) => {
                    if (response.accessToken) {
                        resolve(response.accessToken);
                    }
                }).catch((_error) => {
                    context.acquireTokenRedirect(loginRequest);
                });
            } else {
                console.log('redirect');

                context.loginRedirect(loginRequest);
            }
        }
    });
}
