import * as Msal from 'msal';

export default class AuthB2C {
    constructor() {

        var appConfig = {
            b2cScopes: ["https://b2cmern.onmicrosoft.com/mern/demo.read"],
            webApi: "https://fabrikamb2chello.azurewebsites.net/hello"
        };

        const msalConfig = {
            auth: {
                clientId: "ce5d1a2b-20a7-4f63-adde-6f0168cc29e5",
                authority: "https://b2cmern.b2clogin.com/b2cmern.onmicrosoft.com/B2C_1_signupsignin",
                validateAuthority: false
            },
            cache: {
                cacheLocation: "localStorage",
                storeAuthStateInCookie: true
            }
        };

        this.loginRequest = {
            // scopes: appConfig.b2cScopes
            scopes: ["openid", "profile", "User.Read"]
        };

        // request to acquire a token for resource access
        this.tokenRequest = {
            scopes: appConfig.b2cScopes
        };

        this.applicationConfig = {
            clientID: "ce5d1a2b-20a7-4f63-adde-6f0168cc29e5",
            authority: "https://b2cmern.b2clogin.com/b2cmern.onmicrosoft.com/B2C_1_signupsignin"
        }
        // this.app = new Msal.UserAgentApplication(
        //     this.applicationConfig.clientID,
        //     this.applicationConfig.authority)

        this.app = new Msal.UserAgentApplication(msalConfig);
        this.app.handleRedirectCallback(this.authRedirectCallBack);
    }

    login = () => {
        // return this.app.loginPopup(this.loginRequest).then(function (loginResponse) {
        //     // getToken(tokenRequest).then(updateUI);
        //     console.log(loginResponse)

        // }).catch(function (error) {
        //     console.error(error);
        // });

        this.app.loginRedirect(this.loginRequest);

        // return this.app.loginPopup(this.loginRequest).then(
        //     idToken => {
        //         console.log(idToken);

        //         return null;
        //         // const user = this.app.getUser();
        //         // if (user) {
        //         //     return user;
        //         // } else {
        //         //     return null;
        //         // }
        //     },
        //     () => {
        //         return null;
        //     }
        // );
    };

    authRedirectCallBack = (error, response) => {
        if (error) {
            console.log(error);
        } else {
            if (response.tokenType === "id_token" && this.app.getAccount() && !this.app.isCallback(window.location.hash)) {
                showWelcomeMessage();
                acquireTokenRedirectAndCallMSGraph(graphConfig.graphMeEndpoint, loginRequest);
            } else if (response.tokenType === "access_token") {
                callMSGraph(graphConfig.graphMeEndpoint, response.accessToken, graphAPICallback);
            } else {
                console.log("token type is:" + response.tokenType);
            }
        }
    }

    logout = () => {
        this.app.logout();
    };

    getToken = () => {
        return this.app.acquireTokenSilent(this.applicationConfig.graphScopes).then(
            accessToken => {
                return accessToken;
            },
            error => {
                return this.app
                    .acquireTokenPopup(this.applicationConfig.graphScopes)
                    .then(
                        accessToken => {
                            return accessToken;
                        },
                        err => {
                            console.error(err);
                        }
                    );
            }
        );
    };
}
